    if (indexPrices) {
        Highcharts.chart("chart-2", {
            title: null,
            subtitle: null,
            yAxis: { title: { text: "" } },
            xAxis: { type: "datetime", dateTimeLabelFormats: { month: "%e. %B", year: "%b" }, minRange: 12 },
            time: { useUTC: false },
            legend: { enabled: false },
            plotOptions: { series: { label: { connectorAllowed: false } }, area: { marker: { radius: 2 }, lineWidth: 1, states: { hover: { lineWidth: 1 } }, threshold: null } },
            series: [{ type: "line", name: "Index price", color: "#00A75E", data: JSON.parse(indexPrices.dataset["prices"]).sort() }],
            tooltip: { backgroundColor: "#fff", borderWidth: 0 /*formatter: tooltipFormatter*/ },
            responsive: { rules: [{ condition: { maxWidth: 600 }, chartOptions: { legend: { enabled: false } } }] },
            navigation: false,
        });
    }
    tradingVolumes("tv-total");
    tradingVolumes("tv-stocks");
    tradingVolumes("tv-bonds");
    tradingVolumes("tv-repos");
    function tradingVolumes(id) {
        var elem = document.getElementById(id);
        if (elem) {
            Highcharts.chart(id, {
                title: null,
                subtitle: null,
                xAxis: { type: "datetime" },
                yAxis: { title: { text: "" } },
                time: { useUTC: false },
                legend: { enabled: false },
                plotOptions: {
                    area: {
                        fillColor: {
                            linearGradient: { x1: 0, y1: 0, x2: 0, y2: 1 },
                            stops: [
        [0, '#27AE60'],
        [1, 'rgba(39, 174, 96, 0)']
    ]
                        },
                        marker: { radius: 2 },
                        lineWidth: 1,
                        states: { hover: { lineWidth: 1 } },
                        threshold: null,
                    },
                    series: { label: { connectorAllowed: false } },
                },
                series: [{ showInLegend: false, type: "area", name: "", color: "#00A75E", data: JSON.parse(elem.dataset["volumes"]) }],
                tooltip: { backgroundColor: "#fff", borderWidth: 0 },
                responsive: { rules: [{ condition: { maxWidth: 600 }, chartOptions: { legend: { enabled: false } } }] },
                navigation: false,
            });
        }
    }
    var chart = document.getElementById("profits"),
        instance;
    if (chart) {
        var type = document.getElementById("profits-type"),
            notDuration = document.getElementById("profits-not-duration"),
            diDuration = document.getElementById("profits-di-duration"),
            profits = JSON.parse(chart.dataset["profits"]);
        type.addEventListener("change", profitsChart);
        notDuration.addEventListener("change", profitsChart);
        diDuration.addEventListener("change", profitsChart);
        profitsChart();
    }
    function profitsChart() {
        var isNot = type.value == 1,
            data = profits.filter(function (profit) {
                return profit.type == type.value && profit.duration == (isNot ? notDuration.value : diDuration.value);
            });
        notDuration.hidden = !isNot;
        diDuration.hidden = isNot;
        if (instance) {
            instance.destroy();
        }
        instance = Highcharts.chart(chart, {
            title: null,
            subtitle: null,
            yAxis: { title: { text: "" } },
            xAxis: { type: "datetime", dateTimeLabelFormats: { month: "%e. %B", year: "%b" }, minRange: 12 },
            time: { useUTC: false },
            legend: { enabled: false },
            plotOptions: { series: { label: { connectorAllowed: false } }, area: { marker: { radius: 2 }, lineWidth: 1, states: { hover: { lineWidth: 1 } }, threshold: null } },
            series: [
                {
                    type: "line",
                    name: "Value",
                    color: "#00A75E",
                    data: data
                        .map(function (profit) {
                            return [profit.date, profit.value];
                        })
                        .sort(),
                },
            ],
            tooltip: { valueSuffix: "%", backgroundColor: "#fff", borderWidth: 0 },
            responsive: { rules: [{ condition: { maxWidth: 600 }, chartOptions: { legend: { layout: "horizontal", align: "center", verticalAlign: "bottom" } } }] },
            navigation: false,
        });
    }
})();


</script>

  <script>
  	
  jQuery(window).scroll(function() {
    if ($(window).scrollTop() > $(window).height()) {
        $('.home .fixed-soc').addClass("sticky_soc");
    } else {
        $('.home .fixed-soc').removeClass("sticky_soc");
    }

    if( $(window).scrollTop() > ($('.bottom-banner').offset().top - 500 - $('.bottom-banner').height()) ){
        $('.home .fixed-soc').addClass("hide");
    } else {
        $('.home .fixed-soc').removeClass("hide");
    }
});

  </script>

